import *  as types from './../constants/ActionTypes';
// var data= JSON.parse(localStorage.getItem("Products"));
// var initialState =data ?  data : [];
var initialState = [
        { ID: 11100, Price: 1000, Name: 'samsung s1', Brand: 'SamSung' },
        { ID: 1, Price: 1000, Name: 'samsung s2', Brand: 'SamSung' },
        { ID: 2, Price: 1000, Name: 'samsung s3', Brand: 'SamSung' },
        { ID: 3, Price: 1000, Name: 'samsung s4', Brand: 'SamSung' },
];
var findIndex = (Products, id) => {
        var result = -1;
        Products.forEach((value, index) => {
                if (value.ID.toString() === id.toString())
                        result = index;
        });

        return result;
}
var myReducer = (state = initialState, action) => {
        switch (action.type) {
                case types.LIST_ALL:
                        return state;
                case types.Add_Product:
                        var newProduct = {
                                ID: 0,
                                Price: action.Product.price,
                                Name: action.Product.name,
                                Brand: action.Product.brand
                        }
                        state.push(newProduct);
                        // localStorage.setItem('Products', JSON.stringify(state))
                        return [...state];
                case types.Delete_Product:
                        var id = action.ID;

                        var index = findIndex(state, id);
                        if (index !== -1) {
                                state.splice(index, 1)

                        }
                        return [...state];
                case types.Edit_Product:
                        console.log("aaa");
                        return [...state];
                default: return state;

        }
};
export default myReducer;
