import *  as types from './../constants/ActionTypes';

var initialState = { ID: null, Price: null, Name: '', Brand: '' };
var myReducer =(state = initialState, action)=>{
        switch(action.type){
            case types.Edit_Product:
                console.log("aa: " + JSON.stringify(action.Product));
                return action.Product;
            default: return { ID: null, Price: null, Name: '', Brand: '' };
        }
        
};
export default myReducer;
