import './App.css';
import React, { Component } from "react";

import Header from './component/Header';
import NewFilm from './component/NewFilm';
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listProduct: [
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 1' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 2' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 3' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 4' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 5' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 6' }

      ],
      newList: [],
      keySearch:""
    }
  }
  componentDidMount() {
    this.setState({
      newList: this.state.listProduct
    });
  }
  changgeProduct = (key) => {
    let listSp=[];
    listSp = this.state.listProduct.filter((value, index) => {
      return value.ten.includes(key);
    })
    this.setState(
      {
        newList: listSp
      }
    )
    console.log(listSp);
  }
  render() {
    return (
      <div >
        <Header onChange={this.changgeProduct} />
        <NewFilm dssp={this.state.newList} />
      </div>
    )
  }
}

export default App;
