import *  as types from './../constants/ActionTypes';

var initialState = {}
var myReducer =(state = initialState, action)=>{
        switch(action.type){
            case types.Edit_Product:
                state =action.Product;
                return state;
            default: return state;
        }
};
export default myReducer;
