import './App.css';
import React, { Component } from "react";

import Header from './component/Header';
import NewFilm from './component/NewFilm';
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listProduct: [
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 1' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 2' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 3' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 4' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 5' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 6' }

      ],
      newList: []
    }
  }

  setProduct = () => {
    if(this.state.newList.length > 0){
      this.setState(
        {
          newList: this.state.listProduct

        }
      )
    }
    else
    {
      this.setState(
        {
          newList: this.state.newList
        }
      )
    }
    return newList
    }
  changgeProduct = (key) => {
    let listSp = [];
    listSp = this.state.listProduct.map((value, index) => {
      if (value.ten.includes(key)) {
        return value;
      }
    })
    this.setState(
      {
        newList: listSp
      }
    )
    console.log(listSp);
  }
  render() {
    return (
      <div >
        <Header onChange={this.changgeProduct} />
        <NewFilm dssp={this.setProduct()} />
      </div>
    )
  }
}

export default App;
