import React, { Component } from "react";
class NewFilm extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};
    
        this.handleSubmit = this.handleSubmit.bind(this);
      }
      handleChange=(event) =>{
        this.setState({value: event.target.value});
      }
    
      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
      }
    render() {
       
       
        return (
            
            <div className="NewFilm row">
                {
                    
                    this.props.dssp.map((value, index) => {
                        return(
                            <div className="col-md-2">
                            <img src={value.anh} alt={value.alt} />
                            <h3 className="nameFilm">{value.ten}</h3>
                            
                        </div>
                        )
                    })
                }
                <div className="fb-page">
                    <iframe src="https://www.facebook.com/plugins/page.php?href=https%3A%2F%2Fwww.facebook.com%2FIsokids-102914425225086%2F&tabs&width=340&height=130&small_header=false&adapt_container_width=true&hide_cover=false&show_facepile=true&appId" width={340} height={130} style={{ border: 'none', overflow: 'hidden' }} scrolling="no" frameBorder={0} allowFullScreen="true" allow="autoplay; clipboard-write; encrypted-media; picture-in-picture; web-share" />
                </div>
                <input type="text" value={this.state.value} onChange={this.handleChange} />
        
                <input type="submit" value="Submit"  onClick={this.handleSubmit}/>
            </div>

        )
    }
}
export default NewFilm;
