import './App.css';
import React, { Component } from "react";

import Header from './component/Header';
import NewFilm from './component/NewFilm';
class App extends Component {
  constructor(props) {
    super(props);
    this.changgeProduct=this.changgeProduct.bind(this);

    this.state = {
      listProduct: [
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 1' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 2' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 3' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 4' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 5' },
        { anh: './image/cuteCat.jpg', alt: 'ảnh minh hoạ', ten: 'film 6' }

      ],
      keySearch: ''

    }

  }

  async changgeProduct(value) {
    await this.setState({
      keySearch : value,
      function () {
        console.log(this.state.boardAddModalShow);
      }
    });
    console.log("a: "+ this.setState.keySearch);
    console.log("b: "+ value);

  }

  render() {
    var {listProduct, keySearch} = this.state;
    if(keySearch){
      listProduct=listProduct.filter((listProduct)=>{
        return listProduct.ten.toLowerCase().indexOf(keySearch)!==1
      }
      )
    }
    return (
      <div >
        <Header changgeProduct={this.changgeProduct } />
        <NewFilm dssp={this.state.listProduct} />
      </div>
    )
  }
}

export default App;
